IF EXISTS (SELECT * FROM LANGUAGES WHERE FORM=N'rptPHIEU_BAO_TRI_NT' AND KEYWORD =N'Remark') UPDATE LANGUAGES SET VIETNAM=N'Remark', ENGLISH=N'Remark', VIETNAM_OR=N'Remark', ENGLISH_OR=N'Remark' WHERE FORM=N'rptPHIEU_BAO_TRI_NT' AND KEYWORD=N'Remark' ELSE INSERT INTO LANGUAGES(FORM,KEYWORD,VIETNAM,ENGLISH,VIETNAM_OR,ENGLISH_OR) VALUES(N'rptPHIEU_BAO_TRI_VECO',N'Remark',N'Remark',N'Remark',N'Remark',N'Remark')